import React, { useEffect, useState } from "react";
import { ObjectProducto } from "./ObjectProducto";
import './producto.css';

var pathname = window.location.pathname.split("/");
let id = pathname[pathname.length - 1]

export const Producto = () => {

  const [productos, setProductos] = useState([])
  const fetchApi = async () => {
    const response = await fetch('http://localhost:8080/productos/' + id)
      .then((response) => response.json())
    setProductos(response)
    console.log(response);
  };
  useEffect(() => {
    fetchApi();
  }, [])

  productos = Object.keys(productos).productos((key) => productos[key]);

  return (
    <>
      <main>
        <div class="contenedor-productos">
          {
            Object.keys(productos).forEach(function(producto) {
              <ObjectProducto key={producto.id}
                id={producto.id_product}
                name={producto.name}
                price={producto.price}
                picture={producto.picture_url}
                stock={producto.stock}
                id_category={producto.id_category}
              />
            })
          }
        </div>
      </main>
    </>
  )
}
