import React from "react";
import Productos from "../../images/productos.png";

export const ObjectCategorias = (
    { id,
        name,
        icon,
        cantidad,
    }) => {
    return (
        <a href={`http://localhost:8080/categorias/${name}`} class="boton"
        onMouseOut="OnBotonOver('{name}')" onmouseout="BotonOut('')">
            <div class="contenido-boton" id={name}>
                <div class="div-icon">
                    <img class="icono-categoria" src={icon}/>
                </div>
                <div class="div-info">
                    <p class="letras-categorias">{name}</p>
                    <div class="sub-div-info">
                        <img class="productos" src={Productos}/>
                            <p>
                                {cantidad}
                            </p>
                    </div>
                </div>
            </div>

        </a>
    )
}

function BotonOver(nombre) {
    document.getElementById(nombre).style.transform = "scale(1.1)";
}

function BotonOut(nombre) {
    document.getElementById(nombre).style.transform = "scale(1)";
}
